lsApi.getDecks = (topicName) => {
  const parseThis = res => res.body.map_collections || [];
  return new Promise(
    (resolve, reject) => request
      .get(`${baseUrlRails}topics/${topicName}/map_collections.json`)
      .end((err, res) => err ? reject(err) : resolve(parseThis(res))));
};

